package BT_TongHop;

import java.util.Scanner;

public class Life_the_Universe_and_Everything {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scan = new Scanner(System.in);
		int n;
		do {
			n = scan.nextInt();
			if (n!=42)
				System.out.println(n);
		}while(n!=42);
	}

}
